package loops;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//initialization 
		int i =0;
		
		do {
			System.out.println(i);
			
			//increment/decrement
			i++;
		}
		///condition
		while (i<10);
		}
	}
     //dowhileloop run atleast one time even if condition doesn't met 
